<?php
use \ForceUTF8\Encoding;

function AION_BIBLES_RAWFIX($args, $bible, $type, $file, $vpl, $sword, $unbound, &$bfile, $raw_orig, $raw_fixed, $raw_tags, &$rawcheck) {
// fix custom file issues before processing
// specials = \^$.|?*+[(){}
// bracketed specials = ^-]\


// READ FILE and UTF8 ENCODE BEFORE CHANGES
if (!($bfile = file_get_contents($file))) { AION_ECHO("ERROR! Problem reading: $file"); }
$bfile = Encoding::toUTF8($bfile);

// REMOVE COMMENTS
if (!($bfile = preg_replace("/\n## [^\n]*/us","", $bfile,-1,$rnum))) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
if (!($bfile = preg_replace("/^## [^\n]*/us","", $bfile,-1,$rnum))) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }


// BEFORE REMOVE CHARACTERS
if ('Holy-Bible---English---Trans-Trans'==$bible) {
	if (!($bfile = preg_replace("/\[([[:punct:]]+)\]/us",'$1 ', $bfile,-1,$rnum)) || $rnum!=2) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
	if (!($bfile = preg_replace("/\[[ ]+/us",'[', $bfile,-1,$rnum)) || $rnum!=0) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
	if (!($bfile = preg_replace("/[^ [:punct:]◄]+\[/us",' [', $bfile,-1,$rnum)) || $rnum!=377) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
	if (!($bfile = preg_replace("/[ ]+\]/us",']', $bfile,-1,$rnum)) || $rnum!=343) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
	if (!($bfile = preg_replace("/\][^ [:punct:]►\r\n]+/us",'] ', $bfile,-1,$rnum)) || $rnum!=83) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
	if (!($bfile = preg_replace("/</us",'(',$bfile,-1,$rnum)) || $rnum!=27) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
	if (!($bfile = preg_replace("/>/us",')',$bfile,-1,$rnum)) || $rnum!=27) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
}
if ('Holy-Bible---French---French-Crampon-Bible'==$bible) {
	if (!($bfile = preg_replace("/\*\*\* [^*]+\*\*\*/us",' ',$bfile,-1,$rnum)) || $rnum!=6) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
}
if ('Holy-Bible---Greek---Greek-Nestle'==$bible) {
	if (!($bfile = preg_replace("/[<>]+/us"," ",$bfile,-1,$rnum)) || $rnum!=6) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
}
if ('Holy-Bible---Greek---Greek-Westcott-Hort'==$bible) {
	if (!($bfile = preg_replace("/ [\d]+:[\d]+/us",' ',$bfile,-1,$rnum)) || $rnum!=16) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
	if (!($bfile = preg_replace("/</us",'(',$bfile,-1,$rnum)) || $rnum!=29) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
	if (!($bfile = preg_replace("/>/us",')',$bfile,-1,$rnum)) || $rnum!=29) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
}
if ('Holy-Bible---Portuguese---Portuguese-Trans-Trans'==$bible) {
	if (!($bfile = preg_replace("/\[([[:punct:][:space:]]+)\]/us",'$1 ', $bfile,-1,$rnum)) || $rnum!=143) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
}


// REMOVE CHARACTERS
if ('Holy-Bible---English---STEPBible-Amalgamant'==$bible ||
	'Holy-Bible---English---STEPBible-Concordant'==$bible) {
	$bfile = preg_replace("/[¶]+/us", '', $bfile);
}
else {
	$bfile = preg_replace("/[¶*|]+/us", '', $bfile);
}


// REMOVE HTML TAGS
// make sure no valid data tagged!
if (preg_match_all('/<[^<>\n]*>/us',$bfile,$matches,PREG_PATTERN_ORDER)===FALSE) { AION_ECHO("ERROR! Rawfix preg_match_all(line=".__LINE__."): $file"); }
if (empty($matches[0]) || !is_array($matches[0])) { $matches[0] = array("no tags found"); }
else { $matches[0] = array_unique($matches[0]); asort($matches[0]); }
if (!file_put_contents($raw_tags, "\n\n#######################\n$bible\n".implode("\n", $matches[0]), FILE_APPEND)) { AION_ECHO("ERROR! Rawfix TAGS file_put_contents(line=".__LINE__."): $file"); }


switch($bible) {
case 'Holy-Bible---Bulgarian---Bulgarian-Bible' :
	if (!($bfile = preg_replace('/<([^<>\n]*)>/us','[$1]',$bfile,-1,$rnum)) || $rnum!=12) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
	break;
case 'Holy-Bible---English---New-Heart-Aramaic' :
case 'Holy-Bible---English---New-Heart-Jehovah' :
case 'Holy-Bible---English---New-Heart-Jesus-Messiah' :
case 'Holy-Bible---English---New-Heart-Messianic' :
case 'Holy-Bible---English---New-Heart-Standard' :
case 'Holy-Bible---English---New-Heart-YHWH' :
	if (!($bfile = preg_replace('/</us', '[',$bfile,-1,$rnum)) || $rnum!=116) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
	if (!($bfile = preg_replace('/>/us', ']',$bfile,-1,$rnum)) || $rnum!=116) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
	break;
case 'Holy-Bible---Chinese---Chinese-Union-Version-Simplified' :
case 'Holy-Bible---Chinese---Chinese-Union-Version-Traditional' :
case 'Holy-Bible---Japanese---Japanese-Bungo-yaku' :
case 'Holy-Bible---Japanese---Japanese-Electronic-Network-Bible' :
case 'Holy-Bible---Japanese---Japanese-Kougo-yaku' :
case 'Holy-Bible---Japanese---Japanese-Meiji-yaku' :
case 'Holy-Bible---Japanese---Japanese-Raguet-yaku' :
case 'Holy-Bible---Japanese---New-Japanese-New-Testament' :
case 'Holy-Bible---Korean---Korean-RV' :
case 'Holy-Bible---English---Rotherham-Bible' :
case 'Holy-Bible---Swedish---Swedish-Bible' :
	$rnum = 0;
	if (!($bfile = preg_replace('/<[^<>\n]*>/us','',$bfile,-1))) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $file"); }
	if (!($bfile = preg_replace('/<[^<>\n]*>/us','',$bfile,-1,$rnum))) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $file"); }
	if ($rnum) { AION_ECHO("WARNING! $bible TAGS within TAGS found: $rnum");  }
	break;
default:
	$rnum = 0;
	if (!($bfile = preg_replace('/<[^<>\n]*>/us',' ',$bfile,-1))) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $file"); }
	if (!($bfile = preg_replace('/<[^<>\n]*>/us',' ',$bfile,-1,$rnum))) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $file"); }
	if ($rnum) { AION_ECHO("WARNING! $bible TAGS within TAGS found: $rnum");  }
}


// REMOVE MINI-NOTES
if ('Holy-Bible---Haitian---Haitian-Creole-Bible'!=$bible &&
	'Holy-Bible---Malayalam---Malayalam-Bible'!=$bible &&
	'Holy-Bible---Romanian---Bayash-Luke'!=$bible &&
	'Holy-Bible---Hebrew---Hebrew-Aleppo-Codex'!=$bible &&
	'Holy-Bible---Tamil---Tamil-Bible'!=$bible) {
	if (!($bfile = preg_replace('/\([A-Z[:digit:][:punct:] ]*[[:digit:]]+[A-Z[:digit:][:punct:] ]*\)/us', ' ', $bfile))) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $file"); }
	if (!($bfile = preg_replace('/[([]{1}[[:digit:][:punct:] ]*[)\]]{1}/us', ' ', $bfile))) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $file"); }
}


// FURTHER CLEANUP
if ('Holy-Bible---Assamese---Assamese-Bible'==$bible ||		// cc share alike
	'Holy-Bible---Panjabi---Punjabi-Bible'==$bible) {		// cc share alike
	if (!($bfile = preg_replace('/add foot note ।/us'," ",$bfile,-1))) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $file"); }
	if (!($bfile = preg_replace('/l/us',"।",$bfile,-1))) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $file"); }
}


// SETUP FOR PER BIBLE RAWFIXES
$bfile_saved = $bfile;
$len_original = $len_fixed = mb_strlen($bfile);
$len_delta = 0;
$vcount_org = 0;
$vcount_fix = 0;
$vcount_del = 0;
$average = 0;




// SWITCH ON BIBLE
switch($bible) {




// RAWFIX BIBLE ********************
case "Holy-Bible---Albanian-Tosk---Albanian-Bible" :
if (!($bfile = preg_replace("/aaa up to 37/us","",$bfile,-1,$rnum)) || $rnum!=1) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
$blockfix = <<<EOF
2CO 13:12 Përshëndetni njeri tjetrin me një puthje të shenjtë,
2CO 13:13 të gjithë shenjtorët ju përshëndesin.
2CO 13:14 Hiri i Zotit Jezu Krisht, dashuria e Perëndisë dhe bashkësia e Frymës së Shenjtë qofshin me ju të gjithë. Amen.
2CO 13:15 Pali, apostull (jo nga njerëzit, as me anë të njeriut, por nëpërmjet Jezu Krishtit dhe Perëndisë Atit, që e ringjalli prej së vdekurish),
GAL 1:1 
EOF;
if (!($bfile = preg_replace("/2CO 13:12 (.+?)GAL 1:1 /us",$blockfix,$bfile,-1,$rnum)) || $rnum!=1) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
$blockfix = <<<EOF
HEB 7:8 Dhe, që të them kështu, vetë Levi, që merr të dhjetat, dha të dhjeta te Abrahami,
HEB 7:9 sepse ishte ende në mesin e t’et, kur Melkisedeku i doli përpara.
HEB 7:10 Sepse, po të ishte përkryerja me anë të priftërisë levitike (sepse populli e mori ligji nën atë), ç’nevojë kishte të dilte një prift tjetër sipas rendit të Melkisedekut dhe të mos caktohet sipas rendit të Aaronit?
